angular.module("toDoLists").factory("addTaks", function ($http) {
    var _addTaks = function (toDoList) {
        return $http.post("http://localhost:3001/todolists", toDoList)
    }

    var _showTaks = function () {
        return $http.get("http://localhost:3001/todolists")
    }

    return {
        addTaks: _addTaks,
        showTaks: _showTaks
    }
})